import React, { useEffect } from "react";
import PropTypes from "prop-types";
import Loader from "../ui/Loader/Loader";
import Shelf from "./Shelf";
import * as BooksAPI from "../BooksAPI";

const Home = ({
  shelfs,
  initializeHomeLoader,
  handleShelfs,
  shelfOptionSelected,
  selectingOption,
  selectMenuDisabled,
  homeLoader,
  isAuthenticated,
  history,
}) => {
  useEffect(() => {
    initializeHomeLoader();
    BooksAPI.getAll().then((data) => {
      handleShelfs(data, "home");
    });

    const callHomePage = async () => {
      try {
        if (!isAuthenticated) {
          history.push("/signin");
        }

        const res = await fetch("/", {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          credentials: "include",
        });

        const data = await res.json();
        console.log(data);

        if (res.status !== 200) {
          const error = new Error(res.error);
          throw error;
        }
      } catch (err) {
        console.log(err);
        // history.push("/register");
      }
    };

    callHomePage();
  }, [initializeHomeLoader, handleShelfs, isAuthenticated, history]);

  useEffect(() => {
    localStorage.setItem("shelfs", JSON.stringify(shelfs));
  }, [shelfs]);

  return (
    <div className="home-screen">
      {homeLoader ? (
        <Loader />
      ) : (
        <>
          {selectMenuDisabled && <Loader className="select-shelf-loader" />}
          <div>
            {Object.keys(shelfs).map((shelf) => (
              <Shelf
                key={shelf}
                id={shelf}
                shelfs={shelfs}
                shelfOptionSelected={shelfOptionSelected}
                selectingOption={selectingOption}
                selectMenuDisabled={selectMenuDisabled}
              />
            ))}
          </div>
          <div className="open-search">
            <a href="/search">
              <button>Add a book</button>
            </a>
          </div>
        </>
      )}
    </div>
  );
};

Home.propTypes = {
  shelfs: PropTypes.object.isRequired,
  initializeHomeLoader: PropTypes.func.isRequired,
  handleShelfs: PropTypes.func.isRequired,
  shelfOptionSelected: PropTypes.func.isRequired,
  selectingOption: PropTypes.func.isRequired,
  selectMenuDisabled: PropTypes.bool.isRequired,
  homeLoader: PropTypes.bool.isRequired,
  isAuthenticated: PropTypes.bool.isRequired,
};

export default Home;
