// AIzaSyA33VDxvw_ttY9BCFQ6hPmGvSX46WQT-dE

const API_KEY = 'AIzaSyA33VDxvw_ttY9BCFQ6hPmGvSX46WQT-dE';

export const searchBooks = (query) => {
  const url = `https://www.googleapis.com/books/v1/volumes?q=${encodeURIComponent(query)}&key=${API_KEY}`;

  return fetch(url)
    .then((response) => response.json())
    .then((data) => data.items)
    .catch((error) => {
      console.error('Error fetching book data from Google Books API:', error);
      return [];
    });
};
