import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import Loader from "../ui/Loader/Loader";
import Shelf from "./Shelf";
import * as BooksAPI from "../BooksAPI";
import { useCookies } from "react-cookie";

// import HomePage from './HomePage';

const Home = ({
  shelfs,
  shelfOptionSelected,
  selectingOption,
  selectMenuDisabled,
  homeLoader,
  initializeHomeLoader,
  handleShelfs,
}) => {

  const [cookies] = useCookies(["jwtoken"]);

  useEffect(() => {
    initializeHomeLoader();
    BooksAPI.getAll().then((data) => {
      handleShelfs(data, "home");
    });
  }, [initializeHomeLoader, handleShelfs]);

  useEffect(() => {
    localStorage.setItem("shelfs", JSON.stringify(shelfs));
  }, [shelfs]);

  const allShelfs = Object.keys(shelfs).map((shelf) => (
    <Shelf
      key={shelf}
      id={shelf}
      shelfs={shelfs}
      shelfOptionSelected={shelfOptionSelected}
      selectingOption={selectingOption}
      selectMenuDisabled={selectMenuDisabled}
    />
  ));

  return (
    <div className="home-screen">
      {homeLoader ? (
        <Loader />
      ) : (
        <>
          {selectMenuDisabled && <Loader className="select-shelf-loader" />}
          <div>{allShelfs}</div>
          <div className="open-search">
            <Link to="/search">
              <button>Add a book</button>
            </Link>
          </div>
        </>
      )}
    </div>
  );
};

Home.propTypes = {
  shelfs: PropTypes.object.isRequired,
  initializeHomeLoader: PropTypes.func.isRequired,
  handleShelfs: PropTypes.func.isRequired,
};

export default Home;

