import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Loader from '../ui/Loader/Loader';
import Shelf from './Shelf';
import * as BooksAPI from '../BooksAPI';

function Home(props) {
  const { shelfs, shelfOptionSelected, selectingOption, selectMenuDisabled, initializeHomeLoader, handleShelfs, homeLoader } = props;

  useEffect(() => {
    initializeHomeLoader();
    BooksAPI.getAll().then(data => {
      handleShelfs(data, "home");
    });
  }, [initializeHomeLoader, handleShelfs]);


  const Home=()=>{
    const callHomePage=async()=>{
        try{
            const res=await fetch('/home', {
method:"GET",
headers:{
    
}
            })
        } catch(err){
            console.log(err)
        }
    }
  }

useEffect(()=>{
    callHomePage();
},[]);


  return (
    <div className="home-screen">

      {homeLoader ? <Loader /> : (
        <>
          {selectMenuDisabled && <Loader className="select-shelf-loader" />}
          <div>
            {Object.keys(shelfs).map(shelf => (
              <Shelf
                key={shelf}
                id={shelf}
                shelfs={shelfs}
                shelfOptionSelected={shelfOptionSelected}
                selectingOption={selectingOption}
                selectMenuDisabled={selectMenuDisabled}
              />
            ))}
          </div>
          <div className="open-search">
            <Link to="/search">
              <button>Add a book</button>
            </Link>
          </div>
        </>
      )}

    </div>
  );
}

Home.propTypes = {
  shelfs: PropTypes.object.isRequired,
  initializeHomeLoader: PropTypes.func.isRequired,
  handleShelfs: PropTypes.func.isRequired,
  shelfOptionSelected: PropTypes.func.isRequired,
  selectingOption: PropTypes.func.isRequired,
  selectMenuDisabled: PropTypes.bool.isRequired,
  homeLoader: PropTypes.bool.isRequired,
};

export default Home;


