import React from "react";

const About = () => {
  return (
    <div className="about-page">
      <h1>Welcome to Bookshelf </h1>
      <p>
        The platform provides a collection of books that users can choose from.
        Users have their own profile page where they can view their bookshelf,
        track their reading progress, and manage their account settings.
        Bookshelf provides a user-friendly and convenient platform for book
        lovers to organize and track their reading journey, making it easier to
        manage their favorite books and discover new ones.
      </p>
    </div>
  );
};

export default About;
