import React from "react";
import { BrowserRouter as Router,Route, Switch, Redirect } from "react-router-dom";
import Search from "./Search";
import Home from "./Home";
import About from "./About";
import Header from "./Header";
import Footer from "./Footer";
import Login from "./Login";
import Signup from "./Signup";
import "bootstrap/dist/css/bootstrap.css";
import "./App.css";
import "@fortawesome/fontawesome-svg-core/styles.css";
import RouteGuard from "./RouteGuard";
import { history } from "./history";
import axios from "axios";


const BooksApp = () => {
//check jwt token
const [token, setToken] = useState(localStorage.getItem("token"));

useEffect(() => {
  if (token) {
    setAuthToken(token);
  }
}, [token]);

const setAuthToken = (token) => {
  if (token) {
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  } else {
    delete axios.defaults.headers.common["Authorization"];
  }
};

const [homeLoader, setHomeLoader] = useState(false);
const [searchLoader, setSearchLoader] = useState(false);
const [selectMenuDisabled, setSelectMenuDisabled] = useState(false);
const [books, setBooks] = useState([]);
const [shelfs, setShelfs] = useState({
      currentlyReading: {
        name: "currentlyReading",
        uiName: "Currently Reading",
        data: [],
      },
      wantToRead: {
        name: "wantToRead",
        uiName: "Want to Read",
        data: [],
      },
      read: {
        name: "read",
        uiName: "Read",
        data: [],
      },
    },
);
const selectingOption = () => {
  setSelectMenuDisabled(true);
};

const initializeHomeLoader = () => {
  setHomeLoader(true);
};

const initializeSearchLoader = () => {
  setSearchLoader(true);
};

const handleShelfs = (fetchedBooks, screenName) => {
  let clonedShelfs = { ...shelfs };
  if (screenName === "home") {
    setBooks(fetchedBooks);
  } else {
    setBooks([]);
  }

  fetchedBooks.forEach((book) => {
    const bookShelf = book.shelf;
    if (shelfs[bookShelf]?.name === bookShelf) {
      const clonedShelf = { ...clonedShelfs[bookShelf] };
      clonedShelf.data.push(book);
      clonedShelfs[bookShelf] = clonedShelf;
      setShelfs(clonedShelfs);
    }
  });

  setHomeLoader(false);
  setSearchLoader(false);
};

const shelfOptionSelected = (book, shelf) => {
  let clonedShelfs = { ...shelfs };
  if (book.shelf && book.shelf !== "none") {
    let oldShelf = book.shelf;
    let clonedOldShelf = { ...clonedShelfs[oldShelf] };
    const filteredClonedOldShelfArray = clonedOldShelf.data.filter((shelfBook) => book.id !== shelfBook.id);

    clonedOldShelf.data = filteredClonedOldShelfArray;

    book.shelf = shelf;

    if (shelf !== "none") {
      let clonedNewShelf = { ...clonedShelfs[shelf] };
      clonedNewShelf.data.push(book);

      clonedShelfs = {
        ...clonedShelfs,
        [oldShelf]: clonedOldShelf,
        [shelf]: clonedNewShelf,
      };
    } else {
      clonedShelfs[oldShelf] = clonedOldShelf;
    }
  }

  let clonedBooks = [...books];
  let requiredBook = clonedBooks.find((clonedBook) => book.id === clonedBook.id);
  if (requiredBook) {
    requiredBook.shelf = shelf;
  }

  setShelfs(clonedShelfs);
  setSelectMenuDisabled(false);
  setBooks(clonedBooks);
};

    return (
      <div
        className="app"
        // style={{ backgroundImage: "url(/library.jpg)" }}
      >
        <Header />

        <Router history={history}>
        <Switch>
          <Route exact path="/about">
            <About />
          </Route>
          <Route exact path="/signin">
            <Login />
          </Route>
          <Route exact path="/register">
            <Signup />
          </Route>

          <Route exact path="/">
            <Home
              shelfs={this.state.shelfs}
              handleShelfs={this.handleShelfs}
              initializeHomeLoader={this.initializeHomeLoader}
              homeLoader={this.state.homeLoader}
              shelfOptionSelected={this.shelfOptionSelected}
              selectingOption={this.selectingOption}
              selectMenuDisabled={this.state.selectMenuDisabled}
            />
          </Route>

          <Route exact path="/search">
            <Search
              books={this.state.books}
              shelfOptionSelected={this.shelfOptionSelected}
              selectingOption={this.selectingOption}
              handleShelfs={this.handleShelfs}
              selectMenuDisabled={this.state.selectMenuDisabled}
              resetBooks={this.resetBooks}
              setBooks={this.setBooks}
              initializeSearchLoader={this.initializeSearchLoader}
              searchLoader={this.state.searchLoader}
            />
          </Route>


            <Redirect to="/" />

          <Footer />
        </Switch>
        </Router>
      </div>

    )
};

export default BooksApp;
